import React, { useState, useEffect, useCallback } from 'react';

const GRID_SIZE = 15;
const CELL_SIZE = 20;
const INITIAL_SNAKE = [{ x: 7, y: 7 }];
const INITIAL_DIRECTION = { x: 1, y: 0 };
const INITIAL_FOOD = { x: 5, y: 5 };

const manhattanDistance = (a, b) => Math.abs(a.x - b.x) + Math.abs(a.y - b.y);

const AISnakeGame = () => {
  const [snake, setSnake] = useState(INITIAL_SNAKE);
  const [direction, setDirection] = useState(INITIAL_DIRECTION);
  const [food, setFood] = useState(INITIAL_FOOD);
  const [gameOver, setGameOver] = useState(false);
  const [score, setScore] = useState(0);

  const getNeighbors = (node) => {
    const neighbors = [
      { x: node.x - 1, y: node.y },
      { x: node.x + 1, y: node.y },
      { x: node.x, y: node.y - 1 },
      { x: node.x, y: node.y + 1 },
    ];
    return neighbors.filter(
      (n) =>
        n.x >= 0 &&
        n.x < GRID_SIZE &&
        n.y >= 0 &&
        n.y < GRID_SIZE &&
        !snake.some((s) => s.x === n.x && s.y === n.y)
    );
  };

  const findPath = (start, goal) => {
    const openSet = [start];
    const cameFrom = {};
    const gScore = { [`${start.x},${start.y}`]: 0 };
    const fScore = { [`${start.x},${start.y}`]: manhattanDistance(start, goal) };

    while (openSet.length > 0) {
      const current = openSet.reduce((a, b) =>
        fScore[`${a.x},${a.y}`] < fScore[`${b.x},${b.y}`] ? a : b
      );

      if (current.x === goal.x && current.y === goal.y) {
        const path = [current];
        let temp = current;
        while (cameFrom[`${temp.x},${temp.y}`]) {
          temp = cameFrom[`${temp.x},${temp.y}`];
          path.unshift(temp);
        }
        return path;
      }

      openSet.splice(openSet.indexOf(current), 1);
      const neighbors = getNeighbors(current);

      for (const neighbor of neighbors) {
        const tentativeGScore =
          gScore[`${current.x},${current.y}`] + 1;

        if (
          tentativeGScore <
          (gScore[`${neighbor.x},${neighbor.y}`] || Infinity)
        ) {
          cameFrom[`${neighbor.x},${neighbor.y}`] = current;
          gScore[`${neighbor.x},${neighbor.y}`] = tentativeGScore;
          fScore[`${neighbor.x},${neighbor.y}`] =
            gScore[`${neighbor.x},${neighbor.y}`] +
            manhattanDistance(neighbor, goal);

          if (!openSet.some((n) => n.x === neighbor.x && n.y === neighbor.y)) {
            openSet.push(neighbor);
          }
        }
      }
    }

    return null; // No path found
  };

  const moveSnake = useCallback(() => {
    if (gameOver) return;

    const head = snake[0];
    const path = findPath(head, food);

    if (path && path.length > 1) {
      const nextMove = path[1];
      const newDirection = {
        x: nextMove.x - head.x,
        y: nextMove.y - head.y,
      };
      setDirection(newDirection);
    }

    const newSnake = [...snake];
    const newHead = {
      x: head.x + direction.x,
      y: head.y + direction.y,
    };

    if (
      newHead.x < 0 ||
      newHead.x >= GRID_SIZE ||
      newHead.y < 0 ||
      newHead.y >= GRID_SIZE
    ) {
      setGameOver(true);
      return;
    }

    if (snake.some((segment) => segment.x === newHead.x && segment.y === newHead.y)) {
      setGameOver(true);
      return;
    }

    newSnake.unshift(newHead);

    if (newHead.x === food.x && newHead.y === food.y) {
      setScore((prevScore) => prevScore + 1);
      setFood(getRandomFood(newSnake));
    } else {
      newSnake.pop();
    }

    setSnake(newSnake);
  }, [snake, direction, food, gameOver]);

  useEffect(() => {
    const gameLoop = setInterval(moveSnake, 100);
    return () => clearInterval(gameLoop);
  }, [moveSnake]);

  const getRandomFood = (snake) => {
    let newFood;
    do {
      newFood = {
        x: Math.floor(Math.random() * GRID_SIZE),
        y: Math.floor(Math.random() * GRID_SIZE),
      };
    } while (snake.some((segment) => segment.x === newFood.x && segment.y === newFood.y));
    return newFood;
  };

  const resetGame = () => {
    setSnake(INITIAL_SNAKE);
    setDirection(INITIAL_DIRECTION);
    setFood(INITIAL_FOOD);
    setGameOver(false);
    setScore(0);
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-gray-100">
      <h1 className="text-3xl font-bold mb-4">AI Snake Game</h1>
      <div
        className="border-4 border-gray-800 relative bg-white"
        style={{
          width: GRID_SIZE * CELL_SIZE,
          height: GRID_SIZE * CELL_SIZE,
        }}
      >
        {snake.map((segment, index) => (
          <div
            key={index}
            className="absolute bg-green-500"
            style={{
              width: CELL_SIZE - 1,
              height: CELL_SIZE - 1,
              left: segment.x * CELL_SIZE,
              top: segment.y * CELL_SIZE,
              border: '0.5px solid darkgreen',
            }}
          />
        ))}
        <div
          className="absolute bg-red-500"
          style={{
            width: CELL_SIZE - 1,
            height: CELL_SIZE - 1,
            left: food.x * CELL_SIZE,
            top: food.y * CELL_SIZE,
            border: '0.5px solid darkred',
          }}
        />
      </div>
      <div className="mt-4 text-xl">Score: {score}</div>
      {gameOver && (
        <div className="mt-4">
          <p className="text-xl font-bold text-red-600">Game Over!</p>
          <button
            className="mt-2 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
            onClick={resetGame}
          >
            Play Again
          </button>
        </div>
      )}
      <div className="mt-4 text-sm text-gray-600">
        AI is controlling the snake
      </div>
    </div>
  );
};

export default AISnakeGame;
